
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, CircularProgress, Grid, Card, CardContent, Typography } from '@mui/material';

const AllProducts = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://test-server-api.com/products');
        setProducts(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  return (
    <Container>
      <Typography variant="h2" gutterBottom>All Products</Typography>
      {loading ? (
        <CircularProgress />
      ) : (
        <Grid container spacing={3}>
          {products.map(product => (
            <Grid item key={product.id} xs={12} sm={6} md={4}>
              <Card>
                <CardContent>
                  <Typography variant="h5" component="div">{product.name}</Typography>
                  <Typography variant="body2" color="textSecondary">Category: {product.category}</Typography>
                  <Typography variant="body2" color="textSecondary">Company: {product.company}</Typography>
                  <Typography variant="body2" color="textSecondary">Price: {product.price}</Typography>
                  <Typography variant="body2" color="textSecondary">Rating: {product.rating}</Typography>
                  <Typography variant="body2" color="textSecondary">Availability: {product.availability}</Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
    </Container>
  );
};

export default AllProducts;
